import Header from './components/Header';
import Footer from './components/Footer';
import Button from './components/Button';
import { useEffect, useState } from 'react';
import Tasklist from './components/Tasklist';

function App() {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState("");
  const [welcomeMessage, setWelcomeMessage] = useState("");

  useEffect(() => {
    setWelcomeMessage("Welcome to the Task Tracker App!");
    const timer = setTimeout(() => setWelcomeMessage(""), 3000);
    return () => clearTimeout(timer);
  }, []);

  const addTask = () => {
    if (newTask.trim()) {
      setTasks([
        ...tasks,
        {
          id: Date.now(),
          text: newTask,
          completed: false,
        },
      ]);
      setNewTask("");
    }
  };

  const deleteTask = (id) => {
    if(tasks.find((task) => task.id === id && task.completed)) 
      setTasks(tasks.filter((task) => task.id !== id))
    else 
      alert("Please complete the task before deleting it.");
  };

  const ToggleComplete = (id) => {
    setTasks(
      tasks.map(task =>
        task.id === id ? { ...task, completed: !task.completed } : task
      )
    );
  };

  return (
    <div className="min-h-screen flex flex-col">
      <Header />
      <main className="container mx-auto my-8 flex-grow p-4">
        {welcomeMessage && (
          <div className="bg-blue-100 border-2 border-blue-500 text-blue-700 p-4 mb-4 rounded">
            <p>{welcomeMessage}</p>
          </div>
        )}

        <h2 className="text-2xl mb-4">Dashboard</h2>

        <div className="mb-6 flex gap-2">
          <input
            type="text"
            value={newTask}
            onChange={(e) => setNewTask(e.target.value)}
            placeholder="Enter a new task"
            className="flex-grow p-2 border rounded focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <Button type="add" onClick={addTask} />
        </div>

        <Tasklist
          tasks={tasks}
          onDelete={deleteTask}
          onToggleComplete={ToggleComplete}
        />
      </main>
      <Footer />
    </div>
  );
}

export default App;
