const Button = ({ type, onClick }) => {
  const baseClasses = "text-white font-semibold py-2 px-4 rounded focus:outline-none focus:ring-2 focus:ring-offset-2";

  const styles = {
    add: "bg-green-500 hover:bg-green-600 focus:ring-green-500",
    delete: "bg-red-500 hover:bg-red-600 focus:ring-red-500",
    edit: "bg-yellow-500 hover:bg-yellow-600 focus:ring-yellow-500", // ðŸŒŸ added
  };

  const labels = {
    add: "Add Task",
    delete: "Delete Task",
    edit: "Edit Task", // ðŸŒŸ added
  };

  return (
    <button
      className={`${baseClasses} ${styles[type] || styles.add}`}
      onClick={onClick}
      type="button"
    >
      {labels[type] || "Button"}
    </button>
  );
};

export default Button;
