import Header from './components/Header';
import Footer from './components/Footer';
import Button from './components/Button';
import { useEffect } from 'react';
import { useState } from 'react';

function App() {
  const[tasks, setTasks] = useState([]);
  const[newTask, setNewTask] = useState("");
  const[welcomeMessage, setWelcomeMessage] = useState("");

  useEffect(() => {
    setWelcomeMessage("Welcome to the Task Tracker App!");
    const timer = setTimeout(() => setWelcomeMessage(""), 3000);
    return () => clearTimeout(timer);
  }, []);

  const addTask = () => {
    if (newTask.trim()) {
      setTasks([
        ...tasks,
        { id: Date.now(), 
          text: newTask,
          completed: false,
        },

      ]);
      setNewTask("")
    }
  };

  const deleteTask = (id) => {
    setTasks(tasks.filter(task => task.id !== id));
  };

  return (
    <div className="min-h-screen flex flex-col">   
      <Header />

      <main className="container mx-auto my-8 flex-grow p-4">
        <h2 className="text-2xl mb-4">Dashboard</h2>

        <div className="space-x-4">
          <Button type="add" onClick={() => alert("Task Added")} />
          <Button type="delete" onClick={() => alert("Task Deleted")} />
        </div>
      </main>

      <Footer />
    </div>
  );
}

export default App;
