const Tasklist = ({ tasks, onDelete, onToggleComplete }) => {
  const handleDelete = (task) => {
    if (!task.completed) {
      alert("Please complete the task before deleting it.");
      return;
    }
    onDelete(task.id);
  };

  return (
    <div className="space-y-2">
      {tasks.length === 0 ? (
        <p className="text-gray-500">No tasks yet. Add one above!</p>
      ) : (
        <ul className="divide-y divide-gray-200">
          {tasks.map(task => (
            <li key={task.id} className="py-3 flex items-center justify-between">
              <div className="flex items-center">
                <input 
                  type="checkbox" 
                  checked={task.completed} 
                  onChange={() => onToggleComplete(task.id)} 
                  className="h-5 w-5 text-blue-600 rounded focus:ring-blue-500"
                />
                <span
                  className={`ml-3 ${task.completed ? 'line-through text-gray-400' : 'text-gray-800'}`}
                >
                  {task.text}
                </span>
              </div>

              <button
                onClick={() => handleDelete(task)}
                className="bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-4 rounded focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
              >
                Delete
              </button>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default Tasklist;
